mapper.update:
update Course set CourseCode = #{courseCode} , CourseName = #{courseName} , teacherId = #{teacher}.getId() ,
studentNumber = #{studentNumber} , startWeek = #{startWeek} , endWeek = #{endWeek} , isSingleWeek = #{isSingleWeek} , 
credit = #{credit} , examiningForm = #{examiningForm} , collegeid = #{college}.getId() , majorid = #{major}.getId() , 
teacherid = #{teacher}.getId() , level = #{level} , hasMidExam = #{hasMidExam} , finalExam = #{finalExam} ,
midExamWeight = #{midExamWeight} , finalExamWeight = #{finalExamWeight} , passingCourse = #{passingCourse}
where id = #{id}
