mapper.update:
update teacher
set deleteStatus = 1
where id = #{id}

update teacher
set teacherName = #{teacherName}, gender = #{gender} ,
	telPhone = #{telPhone} , email = #{email} ,
	officeAddress = #{officeAddress} , collegeId = #{college.id}, TeacherPortrait=#{teacherPortrait}, Level=#{level}
where id = #{id}
